<?php/****************************************** * name:        getYtVideoId * description: parses the provided url and extracts the video Id * author:      Anudeep Potlapally * @param:      $url string url of the youtube video * @return:     returns the youtube video Id ******************************************/function getYtVideoId($url){    $url = parse_url($url);    parse_str($url['query'], $query);    return $query['v'];}?>